(()=>{"use strict";class t{constructor(t){this.name=t,this.todos=[]}addTodo(t){this.todos.push(t)}deleteTodo(t){const e=this.todos.findIndex((e=>e.name===t));this.todos.splice(e,1)}}class e{constructor(){this.projectList=[]}addProjects(e){const s=new t(e);this.projectList.push(s)}deleteProjects(t){const e=this.projectList.findIndex((e=>e.name===t));this.projectList.splice(e,1)}updateProject(t,e){const s=this.projectList.find((e=>t===e.name));return s.name=e,s}}class s{static loadUI(){s.projectFormHandler(),s.render()}static projects=new e;static projectFormHandler(){const t=document.getElementById("myForm");t.addEventListener("submit",(e=>{e.preventDefault();const o=document.getElementById("taskName").value;s.projects.addProjects(o),this.render(),t.reset()}),!1)}static render(){const t=document.getElementById("list");t.innerHTML="",s.projects.projectList.map((e=>{t.innerHTML+=`<h1 class="title">${e.name}</h1>`}))}}document.addEventListener("DOMContentLoaded",s.loadUI())})();